month_on_month.map:
function(doc) {
  var d = new Date(doc.created_at);
  var year = d.getFullYear(); // Get the year
  var month = d.getMonth() + 1; // Get the month (0-11), add 1 to match 1-12 range

  emit({ year: year, month: month }, 1);
}

day_of_month.map:
function(doc) {
  var d = new Date(doc.created_at);
  var month = d.getMonth() +1; // Get Month
  var day = d.getDate() +1; // Get Day
  emit({ month: month, day: day }, 1);
}
