time.map:
function(doc) {
  var d = new Date(doc.created_at);
  var weekday = d.getDay(); // Sunday = 0, Monday = 1, ..., Saturday = 6

  if (weekday >= 1 && weekday <= 5) { // Weekday (Monday to Friday)
    emit("weekday", 1);
  } else { // Weekend (Saturday or Sunday)
    emit("weekend", 1);
  }
}

days_of_the_week.map:
function(doc) {
  var d = new Date(doc.created_at);
  var day = d.getDay(); // Sunday = 0, Monday = 1, ..., Saturday = 6

  if (day === 0) {
    emit("Sunday", 1);
  } else if (day === 1) {
    emit("Monday", 1);
  } else if (day === 2) {
    emit("Tuesday", 1);
  } else if (day === 3) {
    emit("Wednesday", 1);
  } else if (day === 4) {
    emit("Thursday", 1);
  } else if (day === 5) {
    emit("Friday", 1);
  } else if (day === 6) {
    emit("Saturday", 1);
  }
}

hours.map:
function(doc) {
  var d = new Date(doc.created_at);
  var hour = d.getHours(); // Get the hour of the day (0-23)

  emit(hour, 1);
}
